pragma solidity ^0.4.24;

contract Validator {

	address public owner;
	uint32 i;

	mapping(address => bool) private sensorRegistry;
	mapping(address => mapping(uint256 => uint256)) private videoHash; 

  constructor( ) public{
		owner = msg.sender;
		i = 0;
	}

// Register address
	function registerAddress(address _addr) public {
		require(!sensorRegistry[_addr]);

		sensorRegistry[_addr] = true;
	}

	function deleteAddress(address _addr) public {
		require(sensorRegistry[_addr]);

		sensorRegistry[_addr] = false;
	}

	function checkRegistered(address _addr) public constant returns(bool){
		return sensorRegistry[_addr];
	}


// Saving sensor hash values;
	event hashSaved(address _addr, uint256 _time, uint256 _hash); 

	function saveRecord(address _addr, uint256 _recordTime, uint256 _recordHash) public {
		require(sensorRegistry[_addr]);
		videoHash[_addr][_recordTime] = _recordHash;

		//sensorStruct({recorder:msg.sender, recordTime:_recordTime, recordHash:_recordHash, flag:1});

		emit hashSaved(msg.sender, uint256(block.timestamp), _recordHash);
	}

	function readHash(address _addr, uint256 _recordTime) public view returns (uint256){
		require(sensorRegistry[_addr]);

		return videoHash[_addr][_recordTime];
	}

	function validateHash(address _addr, uint256 _dataTime, uint256 _dataHash) public view returns (bool){
		require(sensorRegistry[_addr]);

		//bytes32 hashed = keccak256(_dataHash);

		return (videoHash[_addr][_dataTime] == _dataHash);
	}

	function consoleLog() public returns (uint32){
		i = i + 1;
		return i;
	}
}
